import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
    name: 'cart',
    initialState: {
        items: [],
        totalQuantiny: 0,
        totalAmount
    },
    reducers: {
        toggle(state){}
    }
})

export const cartActions = cartSlice.actions

export default cartSlice
